mapper.select:
select

from tm_order
where 1 = 1

    and order_no = #{orderNo}


    and status = #{status}


    and (order_user = #{userId} or order_people = #{userId} )


    and order_user in

      #{orderUser , jdbcType=VARCHAR}



    and order_status in

         #{orderStatus , jdbcType=VARCHAR}



    and delivery_date >= #{deliveryDateStart}


    and delivery_date <= #{deliveryDateEnd}


    and establish_date >= #{establishDateStart}


    and establish_date <= #{establishDateEnd}


    and complete_date >= #{completeDateStart}


    and complete_date <= #{completeDateEnd}



        and examine_status = #{examineStatus}


        and examine_status in ('adopt', 'not')

select

from tm_order
where status = 1

    and (order_user = #{userId} or order_people = #{userId} )


    and order_user = #{orderUser}


    and delivery_date >= #{deliveryDateStart}


    and delivery_date <= #{deliveryDateEnd}


    and establish_date >= #{establishDateStart}


    and establish_date <= #{establishDateEnd}


    and complete_date >= #{completeDateStart}


    and complete_date <= #{completeDateEnd}

SELECT DATE_FORMAT( establish_date, '%m' ) months, count(1) orderNum FROM tm_order WHERE establish_date LIKE CONCAT(CONCAT(#{year}), '%') AND status = 1
AND order_status = #{completed}
GROUP BY months ORDER BY months
