mapper.select:
SELECT
    p.*
FROM
    tm_purchase p
LEFT JOIN tm_order o ON p.order_no = o.order_no
    where o.status = 1

    and p.order_no = #{orderNo}


    and p.purchase_no = #{purchaseNo}


    and p.purchase_status = #{purchaseStatus}


    and p.purchase_personnel = #{purchasePersonnel}


    and p.order_status = #{orderStatus}


    and o.order_user = #{userId}

and p.status = 1

SELECT t.order_no order_no, tp.purchase_no purchase_no, tpd.purchase_number purchase_number, tpd.materiel_name materiel_name, tpd.gram_weight gram_weight, tpd.width_of_cloth width_of_cloth, tpd.unit_price unit_price, tpd.unit unit, tpd.supplier supplier, tp.purchase_personnel purchase_personnel, tu.real_name real_name, tpd.purchase_date purchase_date FROM tm_purchase tp LEFT JOIN tm_purchase_detail tpd ON tp.purchase_no = tpd.purchase_no LEFT JOIN tm_order t ON t.order_no = tp.order_no LEFT JOIN ts_user tu ON tu.pk_id = tp.purchase_personnel WHERE t.STATUS = 1 AND tp.STATUS = 1
and tp.purchase_personnel = #{purchasePersonnel}
and tpd.supplier = #{supplier}
and tpd.purchase_date = #{purchaseDate}
and o.order_user = #{userId}
